pragma solidity ^0.8.0;
import "./Multisig.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title AffiliateVault
 * @dev Handles USDT deposits and controlled payments with limited daily allowance
 */
contract AffiliateVault is Multisig {
    address public SUPER_ADMIN;
    IERC20 public usdtToken;
    uint256 public PRICE = 1 * 1e18;
    uint256 public DAILY_LIMIT = 5000 * 1e18;
    uint256 public dailySpent;
    uint256 public lastResetTimestamp;

    event Deposited(address indexed from, uint256 amount, uint256 timestamp);
    event Paid(address indexed to, uint256 amount, uint256 timestamp);

    constructor(address[] memory _owners, uint256 _numConfirmationsRequired, address _usdtTokenAddress)
        Multisig(_owners, _numConfirmationsRequired)
    {
        SUPER_ADMIN = _owners[0];
        usdtToken = IERC20(_usdtTokenAddress);
    }

    modifier isSuperAdmin() {
        require(msg.sender == SUPER_ADMIN, "Only Super Admin");
        _;
    }

    function deposit(uint256 amount) external {
        require(amount >= PRICE, "Amount below minimum");
        require(usdtToken.allowance(msg.sender, address(this)) >= amount, "Not approved");
        require(usdtToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");
        emit Deposited(msg.sender, amount, block.timestamp);
    }

    function payUser(address recipient, uint256 amount) external isSuperAdmin {
        _enforceDailyLimit(amount);
        require(usdtToken.balanceOf(address(this)) >= amount, "Insufficient balance");
        require(usdtToken.transfer(recipient, amount), "Transfer failed");
        emit Paid(recipient, amount, block.timestamp);
    }

    function _enforceDailyLimit(uint256 amount) internal {
        if (block.timestamp > lastResetTimestamp + 1 days) {
            dailySpent = 0;
            lastResetTimestamp = block.timestamp;
        }
        require(dailySpent + amount <= DAILY_LIMIT, "Daily payout limit exceeded");
        dailySpent += amount;
    }

    function getContractBalance() external view returns (uint256) {
        return usdtToken.balanceOf(address(this));
    }

    function getRemainingDailyQuota() external view returns (uint256) {
        if (block.timestamp > lastResetTimestamp + 1 days) {
            return DAILY_LIMIT;
        }
        return DAILY_LIMIT - dailySpent;
    }

    function setMinPrice(uint256 newPrice) external onlyAdmin {
        require(newPrice >= 1e18, "Minimum must be >= 1 USDT");
        PRICE = newPrice;
    }

    function setDailyLimit(uint256 newLimit) external onlyAdmin {
        require(newLimit > 0, "Invalid limit");
        DAILY_LIMIT = newLimit;
    }
}
